type Hex = `0x${string}`;
type BasePermission = {
    type: string;
    isAdjustmentAllowed: boolean;
    data: Record<string, any>;
};
type Rule = {
    type: string;
    data: Record<string, any>;
};
type MetaMaskBasePermissionData = {
    justification?: string | null;
};
type NativeTokenStreamPermission = BasePermission & {
    type: 'native-token-stream';
    data: MetaMaskBasePermissionData & {
        initialAmount?: Hex | null;
        maxAmount?: Hex | null;
        amountPerSecond: Hex;
        startTime?: number | null;
    };
};
type NativeTokenPeriodicPermission = BasePermission & {
    type: 'native-token-periodic';
    data: MetaMaskBasePermissionData & {
        periodAmount: Hex;
        periodDuration: number;
        startTime?: number | null;
    };
};
type Erc20TokenStreamPermission = BasePermission & {
    type: 'erc20-token-stream';
    data: MetaMaskBasePermissionData & {
        initialAmount?: Hex | null;
        maxAmount?: Hex | null;
        amountPerSecond: Hex;
        startTime?: number | null;
        tokenAddress: Hex;
    };
};
type Erc20TokenPeriodicPermission = BasePermission & {
    type: 'erc20-token-periodic';
    data: MetaMaskBasePermissionData & {
        periodAmount: Hex;
        periodDuration: number;
        startTime?: number | null;
        tokenAddress: Hex;
    };
};
type Erc20TokenRevocationPermission = BasePermission & {
    type: 'erc20-token-revocation';
    data: MetaMaskBasePermissionData;
};
type PermissionTypes = NativeTokenStreamPermission | NativeTokenPeriodicPermission | Erc20TokenStreamPermission | Erc20TokenPeriodicPermission | Erc20TokenRevocationPermission;
type PermissionRequest<TPermission extends PermissionTypes> = {
    chainId: Hex;
    from?: Hex;
    to: Hex;
    permission: TPermission;
    rules?: Rule[] | null;
};
type PermissionResponse<TPermission extends PermissionTypes> = PermissionRequest<TPermission> & {
    context: Hex;
    dependencies: {
        factory: Hex;
        factoryData: Hex;
    }[];
    delegationManager: Hex;
};
type RevokeExecutionPermissionRequestParams = {
    permissionContext: Hex;
};
type RevokeExecutionPermissionResponseResult = Record<string, never>;

export type { BasePermission, Erc20TokenPeriodicPermission, Erc20TokenRevocationPermission, Erc20TokenStreamPermission, Hex, MetaMaskBasePermissionData, NativeTokenPeriodicPermission, NativeTokenStreamPermission, PermissionRequest, PermissionResponse, PermissionTypes, RevokeExecutionPermissionRequestParams, RevokeExecutionPermissionResponseResult, Rule };
